/**
 */
var AbstractDataConnection = require('./AbstractDataConnection.js')
	, JavAPRSISConnection = require('./JavAPRSISConnection.js')
	;

function APRSDataConnectionFactory() { }

APRSDataConnectionFactory.prototype.CreateDataConnection = function(args) {
	if(args != null && typeof(args) === 'object') { 
		if(args['connectionType'] == 'APRSIS') {	// The arg value is APRSIS as this also works with APRSC servers
			return new JavAPRSISConnection(args);
		} else {
			throw new Error('Unrecognized connection type argument');
		}
	} else {
		throw new Error('Invalid arguments.  \'args\' must be a key/value array.');
	}
};

module.exports = APRSDataConnectionFactory;