var AbstractAPRSMessage = require('./AbstractAPRSMessage.js');

function APRSPositionReport() {
	// Use these to calculate the symbol
	this.symbolTableId = null;
	this.symbolCode = null;
	
	this.isIconRotatable = false;
	
	this.latitude = null;
	this.parallel = '';
	this.longitude = null;
	this.meridian = '';
	
	this.positionAmbiguity = 0;
	
	this.speed = 0;
	this.direction = 0;
	this.altitude = 0;
	
	this.message = '';
	
	AbstractAPRSMessage.apply(this, arguments);
}

APRSPositionReport.prototype = new AbstractAPRSMessage();
APRSPositionReport.prototype.constructor = APRSPositionReport;

module.exports = APRSPositionReport;