function AbstractAPRSMessage( ) {
	// this.sourceType = null;
	this.receivedTime = new Date();
	this.rawPacket = '';
	this.gpsTimestamp = '';
	
	this.callsign = '';
	
	this.destination = '';
	this.digipeaters = new Array();
	this.messageType = '';
}

AbstractAPRSMessage.prototype.destination = function() {
	if(digipeaters != null && digipeaters.length > 0) {
		return this.digipeaters[0];
	} else {
		return '';
	}
}

module.exports = AbstractAPRSMessage;