var instance = null;

function AbstractAPRSMessage( ) {
    instance = this;
    
	// this.sourceType = null;
	instance.receivedTime = new Date();
	instance.rawPacket = '';
	instance.gpsTimestamp = '';
	
	instance.callsign = '';
	
	instance.destination = '';
	instance.digipeaters = new Array();
	instance.messageType = '';
}

AbstractAPRSMessage.prototype.destination = function() {
	if(instance.digipeaters != null && instance.digipeaters.length > 0) {
		return this.digipeaters[0];
	} else {
		return '';
	}
}

module.exports = AbstractAPRSMessage;